    {
        Console.WriteLine("Page images extraction isn't supported");
        return;
    }

    // Iterate over images
    foreach (PageImageArea image in images)
    {
        // Save the image to the file
        image.Save(Guid.NewGuid().ToString() + image.FileType.Extension);
    }
}

